import java.util.Arrays;

public class MaximumDeletions {
    public static int maximumDeletionsMemoization(String str){
        int n = str.length();
        int[] dp = new int[n+1];
        Arrays.fill(dp,-1);
        return recursionHelper(0,n,str,dp);
    }
    private static int recursionHelper(int i, int n, String str,int[] dp){
        if(i==n) return 0;
        int max = 1;
        if(dp[i]!=-1) return dp[i];
        for(int len = 1; i + 2 * len <= n; len++){
            if(checkIfSame(i, i + len, len, str)){
                int cost = 1+recursionHelper(i+len,n,str,dp);
                max = Math.max(max,cost);
            }
        }
        return dp[i] = max;
    }
    private static boolean checkIfSame(int i, int j, int len, String str) {
        for (int k = 0; k < len; k++) {
            if (str.charAt(i + k) != str.charAt(j + k)) {
                return false;
            }
        }
        return true;
    }
    public static int maximumDeletionsTabulation(String str){
        int n = str.length();
        int[] dp = new int[n+1];

        for(int i = n-1;i>=0;i--){
            int max = 1;
            for(int len = 1; i + 2 * len <= n; len++){
                if(checkIfSame(i, i + len, len, str)){
                    int cost = 1+dp[i+len];
                    max = Math.max(max,cost);
                }
            }
            dp[i] = max;
        }
        return dp[0];
    }
    public static void main(String[] args){
        String str = "";
        System.out.println(maximumDeletionsMemoization(str));
        System.out.println(maximumDeletionsTabulation(str));
    }
}